mapper.insert:

    SELECT LAST_INSERT_ID()

INSERT INTO staff (id, specialty_id, hired_at) VALUES (#{id}, #{specialty.id}, #{hiredAt});
INSERT INTO doctor (staff_id, license_code) VALUES (#{id}, #{licenseCode});

mapper.update:
UPDATE person SET name = #{name}, age = #{age} WHERE id = #{id};
UPDATE staff SET specialty_id = #{specialty.id}, hired_at = #{hiredAt} WHERE id = #{id};
UPDATE doctor SET license_code = #{licenseCode} WHERE staff_id = #{id};
